import React, { useContext, useEffect, useState } from "react";
import { Carousel, Modal, message } from "antd";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import { useHistory, useParams } from "react-router-dom";
import { Context } from "../../context/context";
import { BASE_URL, axiosInstance } from "../../utils/axiosIntance";
import Card from "../../components/card";
import Compare from "../../components/compare";

const ComparePro = () => {
  const history = useHistory();
  const { dil } = useContext(Context);
  const { pro1, pro2, pro3 } = useParams();
  const market = JSON.parse(localStorage.getItem("market"));
  const [banners, setBanners] = useState([]);
  const [categories, setCategories] = useState([]);
  const [product1, setProduct1] = useState();
  const [product2, setProduct2] = useState();
  const [product3, setProduct3] = useState();

  useEffect(() => {
    getBanner();
    getCategories();
  }, []);

  useEffect(() => {
    getPro1();
    getPro2();
    getPro3();
  }, [pro1, pro2, pro3]);

  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then((data) => {
        setCategories(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBanner = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 4,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBanners(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const ScroollRight = (refNewProduct) => {
    refNewProduct.current.scrollBy(200, 0);
  };
  const ScroollLeft = (refNewProduct) => {
    refNewProduct.current.scrollBy(-200, 0);
  };

  const getPro1 = () => {
    let id = pro1?.slice(5) ? pro1?.slice(5) : 0;
    axiosInstance
      .get("/api/product/" + id)
      .then((data) => {
        setProduct1(data.data);
        console.log("pro1", data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getPro2 = () => {
    let id = pro2?.slice(5) ? pro2?.slice(5) : 0;
    axiosInstance
      .get("/api/product/" + id)
      .then((data) => {
        setProduct2(data.data);
        console.log("pro2", data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getPro3 = () => {
    let id = pro3?.slice(5) ? pro3?.slice(5) : 0;
    axiosInstance
      .get("/api/product/" + id)
      .then((data) => {
        setProduct3(data.data);
        console.log("pro3", data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const remove1 = () => {
    history.push({
      pathname:
        "/compare/pro1=" +
        "/pro2=" +
        (pro2?.slice(5).length > 0 ? pro2?.slice(5) : "") +
        "/pro3=" +
        (pro3?.slice(5).length > 0 ? pro3?.slice(5) : ""),
    });
    setProduct1();
  };
  const remove2 = () => {
    history.push({
      pathname:
        "/compare/pro1=" +
        (pro1?.slice(5).length > 0 ? pro1?.slice(5) : "") +
        "/pro2=" +
        "/pro3=" +
        (pro3?.slice(5).length > 0 ? pro3?.slice(5) : ""),
    });
    setProduct2();
  };
  const remove3 = () => {
    history.push({
      pathname:
        "/compare/pro1=" +
        (pro1?.slice(5).length > 0 ? pro1?.slice(5) : "") +
        "/pro2=" +
        (pro2?.slice(5).length > 0 ? pro2?.slice(5) : "") +
        "/pro3=",
    });
    setProduct3();
  };
  return (
    <div className="w-full">
      <h1 className="w-full my-[30px] md:my-11 text-[20px] md:text-[40px] font-[700] text-black">
        Deňeşdirme
      </h1>

      <div className="w-full">
        <Compare />
      </div>
    </div>
  );
};

export default ComparePro;
