import React, { useContext, useEffect, useState } from "react";
import { Carousel, Modal, message } from "antd";
import { useHistory } from "react-router-dom";
import { Context } from "../../context/context";
import { BASE_URL, axiosInstance } from "../../utils/axiosIntance";

const Compare = () => {
  const history = useHistory();
  const { dil } = useContext(Context);
  const [banners, setBanners] = useState([]);

  useEffect(() => {
    getBanner();
  }, []);

  const getBanner = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 4,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBanners(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="w-full">
      <h1 className="w-full my-11 text-[40px] font-[700] text-black">
        Deňeşdirme sahypasy
      </h1>
      <div className="w-full h-[330px] rounded-[10px] bg-footerBackground ">
        <Carousel
          slide="2"
          autoplay
          speed={1000}
          autoplaySpeed={5000}
          dots={false}
          fade={true}
          effect="fade"
          infinite={true}
        >
          {banners.map((item) => {
            console.log("banner", item);
            return (
              <div
                // onClick={() => history.push({ pathname: item?.link })}
                className="block w-full px-1 cursor-pointer"
              >
                <div className="!w-full flex justify-center items-center bg-footerBackground   h-[330px]">
                  <img
                    className=" h-[330px] object-contain"
                    src={BASE_URL + "/" + item?.img}
                    alt=""
                  />
                </div>
              </div>
            );
          })}
        </Carousel>
      </div>
    </div>
  );
};

export default Compare;
