import React, { useContext, useEffect, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import { Carousel } from "antd";
import CategoryCard from "./categoryCard";
import Card from "../../components/card";
import banner from "../../images/banner.png";
import banner1 from "../../images/banner1.png";
import bannerBig from "../../images/bannerBig.png";
import { BASE_URL, axiosInstance } from "../../utils/axiosIntance";
import { Context } from "../../context/context";
const Home = () => {
  const history = useHistory();
  const { dil } = useContext(Context);
  const [banners, setBanners] = useState({
    banners: [],
    bannerCard: [],
    bannerWithoutText: [],
  });
  const [categories, setCategories] = useState([]);
  const [newProducts, setNewProducts] = useState([]);
  const [discountProducts, setDiscountProducts] = useState([]);
  const [favCategories, setFavCategories] = useState([]);
  const [catProducts, setCatProducts] = useState([]);

  const refNewProduct = useRef(null);
  const refDicountProduct = useRef(null);

  useEffect(() => {
    getBanners();
    getBannerCards();
    getCategories();
    getNewProducts();
    getDiscountProducts();
    getCategoriesPro();
  }, []);

  const getBanners = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 1,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBanners({ ...banners, banners: data.data });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBannerCards = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 2,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBanners({ ...banners, bannerCard: data.data });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then((data) => {
        console.log(data.data);
        setCategories(data.data);
        let newArray = data.data.filter((item) => {
          return item.is_favorite == true;
        });
        setFavCategories([...newArray]);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getNewProducts = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          is_new: true,
        },
      })
      .then((data) => {
        console.log(data.data);
        setNewProducts(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getDiscountProducts = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          is_discount: true,
        },
      })
      .then((data) => {
        console.log(data.data);
        setDiscountProducts(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const ScroollRight = (refNewProduct) => {
    refNewProduct.current.scrollBy(200, 0);
  };
  const ScroollLeft = (refNewProduct) => {
    refNewProduct.current.scrollBy(-200, 0);
  };

  const getCategoriesPro = () => {
    favCategories?.map(async (item) => {
      const data = await getProductsOfCat(item.id);
      let obj = { category: item, pro: data };
      let arr = catProducts;
      arr.push(obj);
      setCatProducts([...arr]);
    });
  };

  const getProductsOfCat = (id) => {
    axiosInstance
      .get("/api/product/all", { params: { CategoryId: id } })
      .then((data) => {
        return data.data;
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="w-full pb-4">
      <Carousel autoplay speed={5000}>
        {banners?.banners?.map((item) => {
          return (
            <div
              onClick={() => history.push({ pathname: item?.link })}
              className="block w-full px-1 cursor-pointer"
            >
              <div className="!w-full flex justify-center items-center bg-footerBackground h-[630px]">
                <img
                  className="h-[630px] object-contain"
                  src={BASE_URL + "/" + item?.img}
                  alt=""
                />
              </div>
            </div>
          );
        })}
      </Carousel>
      <div className="w-full my-8 flex gap-8 justify-start overflow-x-auto scrollbar-hide">
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-8" />

      <div className="w-full flex my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">
          Täze gelen harytlar
        </h1>
        <div className="flex gap-4">
          <div
            onClick={() => ScroollLeft(refNewProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center"
          >
            <LeftOutlined className="text-black font-[900]" />
          </div>
          <div
            onClick={() => ScroollRight(refNewProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center"
          >
            <RightOutlined className="text-black font-[900]" />
          </div>
        </div>
      </div>
      <div
        ref={refNewProduct}
        className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide"
      >
        {newProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {newProducts?.map((item) => {
          return <Card data={item} />;
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full flex mt-8 my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">
          Arzanladyşly harytlar
        </h1>
        <div className="flex gap-4">
          <div
            onClick={() => ScroollLeft(refDicountProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center"
          >
            <LeftOutlined className="text-black font-[900]" />
          </div>
          <div
            onClick={() => ScroollRight(refDicountProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center"
          >
            <RightOutlined className="text-black font-[900]" />
          </div>
        </div>
      </div>
      <div
        ref={refDicountProduct}
        className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide"
      >
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full my-12 flex items-center justify-between">
        {banners.bannerCard?.map((item) => {
          return (
            <img
              className="w-[612px] object-contain  rounded-[10px]"
              src={BASE_URL + "/" + item?.img}
              alt="banner"
            />
          );
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full flex mt-8 my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">
          Meşhur kategoriýalar
        </h1>
      </div>
      <div className="w-full gap-4 my-8 flex flex-wrap justify-between items-center ">
        {favCategories?.map((item, i) => {
          return (
            <div
              key={"favCat" + i}
              className="w-[49%] cursor-pointer mb-4 h-[150px]  p-3 flex gap-8 items-center bg-footerBackground rounded-[10px]"
            >
              <div className="w-[126px] flex justify-center items-center p-2 h-[126px] bg-white rounded-[7px]">
                <img
                  className="h-[85%] object-contain"
                  src={BASE_URL + "/" + item.img}
                  alt=""
                />
              </div>
              <div>
                <h1 className="text-[24px] font-[600] text-black pb-2">
                  {dil == "tm"
                    ? item.name_tm
                    : dil == "ru"
                    ? item.name_ru
                    : item.name_en}
                </h1>
                <p className="text-[16px] text-black font-[400]">
                  {item?.Products.length} harytlar
                </p>
              </div>
            </div>
          );
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full flex mt-8 my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">
          {dil == "tm"
            ? catProducts[0].category?.name_tm
            : dil == "ru"
            ? catProducts[0].category?.name_ru
            : catProducts[0].category?.name_en}
        </h1>
        <div className="flex gap-4">
          <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
            <LeftOutlined className="text-black font-[900]" />
          </div>
          <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
            <RightOutlined className="text-black font-[900]" />
          </div>
        </div>
      </div>
      <div className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide">
        {catProducts[0]?.pro?.map((item) => {
          return <Card data={item} />;
        })}
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full !my-12 flex h-[630px] justify-center items-center bg-footerBackground">
        <img
          className="w-full h-[630px] object-cover"
          src={bannerBig}
          alt="bannerBig"
        />
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />

      <div className="w-full flex mt-8 my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">Kondisonerler</h1>
        <div className="flex gap-4">
          <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
            <LeftOutlined className="text-black font-[900]" />
          </div>
          <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
            <RightOutlined className="text-black font-[900]" />
          </div>
        </div>
      </div>
      <div className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide">
        <Card />
        <Card />
        <Card />
        <Card />
        <Card />
        <Card />
      </div>

      <hr className="text-footerBackground bg-footerBackground my-12" />
    </div>
  );
};

export default Home;
