import React, { useContext, useEffect, useRef, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import { Carousel, Modal } from "antd";
import Slider from "react-slick";
import CategoryCard from "./categoryCard";
import BrandCard from "./brarndCard";
import Card from "../../components/card";
import banner from "../../images/banner.png";
import banner1 from "../../images/banner1.png";
import bannerBig from "../../images/bannerBig.png";
import { BASE_URL, axiosInstance } from "../../utils/axiosIntance";
import { Context } from "../../context/context";

const Home = () => {
  const history = useHistory();
  const { dil } = useContext(Context);
  const path = useLocation();
  console.log(path);
  const [openLogin, setopenLogin] = useState(
    path.pathname == "/login" ? true : false
  );
  const [openSign, setopenSign] = useState(
    path.pathname == "/sign" ? true : false
  );
  const [login, setLogin] = useState({ phone: "+993", password: "" });
  const [sign, setSign] = useState({
    phone: "+993",
    code: "",
    password: "",
    password2: "",
  });
  const [banners, setBanners] = useState([]);
  const [bannerCard, setBannerCard] = useState([]);
  const [banner2, setBanner2] = useState([]);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [newProducts, setNewProducts] = useState([]);
  const [discountProducts, setDiscountProducts] = useState([]);
  const [favCategories, setFavCategories] = useState([]);

  const refNewProduct = useRef(null);
  const refDicountProduct = useRef(null);
  const refCat1 = useRef(null);
  const refCat2 = useRef(null);

  const settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    autoplay: true,
    slidesToShow: 2,
    arrows: false,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          infinite: true,
        },
      },
    ],
  };

  useEffect(() => {
    getBanners();
    getBannerCards();
    getBanners2();
    getCategories();
    getBrands();
    getNewProducts();
    getDiscountProducts();
  }, []);

  const getBanners = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 1,
        },
      })
      .then((data) => {
        console.log("banner home", data.data);
        setBanners(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBannerCards = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 2,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBannerCard(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBanners2 = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 3,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBanner2(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then(async (data) => {
        console.log(data.data);
        setCategories(data.data);
        let newArray = data.data.filter((item) => {
          return item.is_favorite == true;
        });
        console.log("newArray favv:::", newArray);
        setFavCategories([...newArray]);
        console.log("newArray22", favCategories);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getBrands = () => {
    axiosInstance
      .get("/api/brand/all")
      .then(async (data) => {
        console.log(data.data);
        setBrands(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

