import React, { useContext, useEffect, useState } from "react";
import { axiosInstance } from "../utils/axiosIntance";
import { Context } from "../context/context";

const Compare = (props) => {
  const { dil } = useContext(Context);
  const [data, setData] = useState({});
  useEffect(() => {
    getCompare();
  }, [props]);
  const getCompare = () => {
    axiosInstance
      .get("/api/compare/all", {
        params: {
          ProductId: props?.ProductId,
        },
      })
      .then((data) => {
        setData(data.data);
        console.log(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="w-full">
      <h1 className="w-full">
        {dil == "TM"
          ? data?.name_tm
          : dil == "RU"
          ? data?.name_ru
          : data?.name_en}
      </h1>
      <div className="w-full justify-center gap-5">
        <div className="w-[300px] flex justify-center">
          <img className="w-full object-contain" src="" alt="" />
          <h1 className="w-full text-left text-[18px] font-[600] text-black">
            Sony 75” BRAVIA XR Master Series Z9J
          </h1>
          <h1 className="w-full text-center text-[16px] font-[700] text-black">
            21,000 TMT
          </h1>
          <button className="flex px-6 h-[50px] bg-black text-white  text-[16px] font-[500] gap-3 justify-center items-center rounded-[45px]">
            <svg
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fill-rule="evenodd"
                clip-rule="evenodd"
                d="M10.5 17.9927C9.672 17.9927 9 18.6647 9 19.4927C9 20.3207 9.672 20.9927 10.5 20.9927C11.328 20.9927 12 20.3207 12 19.4927C12 18.6647 11.328 17.9927 10.5 17.9927Z"
                fill="white"
              />
              <path
                fill-rule="evenodd"
                clip-rule="evenodd"
                d="M17.5 17.9927C16.672 17.9927 16 18.6647 16 19.4927C16 20.3207 16.672 20.9927 17.5 20.9927C18.328 20.9927 19 20.3207 19 19.4927C19 18.6647 18.328 17.9927 17.5 17.9927Z"
                fill="white"
              />
              <path
                d="M3 5H5.331C6.297 5 7.126 5.691 7.299 6.642L8.701 14.358C8.874 15.309 9.703 16 10.669 16H17.438C18.356 16 19.156 15.375 19.379 14.485L21 8"
                stroke="white"
                stroke-width="2"
                stroke-linecap="round"
                stroke-linejoin="round"
              />
              <path
                d="M17 7.99268L14 10.9927L11 7.99268"
                stroke="white"
                stroke-width="2"
                stroke-linecap="round"
                stroke-linejoin="round"
              />
              <path
                d="M14 10.9927V4.99268"
                stroke="white"
                stroke-width="2"
                stroke-linecap="round"
                stroke-linejoin="round"
              />
            </svg>
            Sebede goş
          </button>
        </div>
      </div>
    </div>
  );
};

export default Compare;
