import React, { useEffect, useRef, useState, useContext } from "react";
import { Context } from "../context/context";
import phone from "../images/phone.svg";
import arrowdown from "../images/arrowdown.svg";
import arrowdownblack from "../images/arrowdownblack.svg";
import user from "../images/user.png";
import compare from "../images/compare.png";
import favarite from "../images/favarite.png";
import search from "../images/search.png";
import basket from "../images/basket.png";

const Header = () => {
  const [open, setOpen] = useState(false);
  const { dil, ChangeDil } = useContext(Context);
  function useOutsideAlerter(ref) {
    useEffect(() => {
      /**
       * Alert if clicked on outside of element
       */
      function handleClickOutside(event) {
        if (ref.current && !ref.current.contains(event.target)) {
          setOpen(false);
        }
      }
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref]);
  }
  const wrapperRef = useRef(null);
  useOutsideAlerter(wrapperRef);
  return (
    <div className="w-full">
      <div className="bg-black w-full">
        <div className="w-[90%] mx-auto h-[30px] flex justify-between">
          <div className="flex gap-2 justify-start items-center">
            <img
              src={phone}
              className="w-[16px] object-contain text-whitetext"
              alt="phone"
            />
            <p className="text-whitetext text-[12px] font-[500]">
              +993 62 123456
            </p>
          </div>
          <div className="flex gap-2 justify-start items-center">
            <p className="text-whitetext text-[12px] font-[500]">
              Ãhli harytlartda 10% arzaladyÅ !!!
            </p>
          </div>
          <div
            onClick={() => setOpen(!open)}
            ref={wrapperRef}
            className="flex cursor-pointer gap-[4px] justify-start items-center relative"
          >
            <p className="text-whitetext text-[12px] font-[500]">
              {dil == "TM" ? "TÃ¼rkmen" : dil == "RU" ? "Rusiion" : "English"}
            </p>
            <img
              src={arrowdown}
              className="w-[9px] object-contain text-whitetext"
              alt="arrowdown"
            />
            {open && (
              <div className="absolute shadow-sm top-[30px] right-0 w-[200px] bg-white p-2 z-10">
                <div
                  onClick={() => ChangeDil("TM")}
                  className="w-full text-center bg-white hover:bg-black text-black hover:text-white font-[600] p-2 text-[14px] rounded-[6px]"
                >
                  Turkmen
                </div>
                <div
                  onClick={() => ChangeDil("EN")}
                  className="w-full text-center bg-white hover:bg-black text-black hover:text-white font-[600] p-2 text-[14px] rounded-[6px]"
                >
                  English
                </div>
                <div
                  onClick={() => ChangeDil("RU")}
                  className="w-full text-center bg-white hover:bg-black text-black hover:text-white font-[600] p-2 text-[14px] rounded-[6px]"
                >
                  Russion
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="w-[90%] mx-auto h-[90px] justify-between flex items-center">
        <div>E-commerce</div>
        <div className="w-[600px] flex justify-between items-center bg-[#F9F9F9] h-[40px] rounded-[6px] p-[3px]">
          <div className="relative text-[14px] gap-4 text-black justify-between h-[34px] rounded-[4px] px-4 bg-[#E9E9E9] flex items-center">
            <div>Kategoriya</div>
            <img
              src={arrowdownblack}
              className="w-[10px] z-10 text-black object-contain"
              alt="arrow"
            />
          </div>
          <input
            type="text"
            className="h-[40px] outline-none px-2 w-full bg-[#F9F9F9]"
          />
          <img
            src={search}
            className="w-[24px] cursor-pointer object-contain mr-2"
            alt="search"
          />
        </div>
      </div>
    </div>
  );
};

export default Header;
