import React, { useContext, useEffect, useRef, useState } from "react";
import { useHistory } from "react-router-dom";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import { Carousel } from "antd";
import Slider from "react-slick";
import CategoryCard from "./categoryCard";
import Card from "../../components/card";
import banner from "../../images/banner.png";
import banner1 from "../../images/banner1.png";
import bannerBig from "../../images/bannerBig.png";
import { BASE_URL, axiosInstance } from "../../utils/axiosIntance";
import { Context } from "../../context/context";
const Home = () => {
  const history = useHistory();
  const { dil } = useContext(Context);
  const [banners, setBanners] = useState([]);
  const [bannerCard, setBannerCard] = useState([]);
  const [categories, setCategories] = useState([]);
  const [newProducts, setNewProducts] = useState([]);
  const [discountProducts, setDiscountProducts] = useState([]);
  const [favCategories, setFavCategories] = useState([]);

  const refNewProduct = useRef(null);
  const refDicountProduct = useRef(null);
  const refCat1 = useRef(null);
  const refCat2 = useRef(null);

  const settings = {
    className: "center",
    center: true,
    autoplay: true,
    swipeToSlide: true,
    autoplaySpeed: 3000,
    pauseOnHover: true,
    Infinity: true,
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  useEffect(() => {
    getBanners();
    getBannerCards();
    getCategories();
    getNewProducts();
    getDiscountProducts();
  }, []);

  const getBanners = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 1,
        },
      })
      .then((data) => {
        console.log("banner home", data.data);
        setBanners(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBannerCards = () => {
    axiosInstance
      .get("/api/banner/all", {
        params: {
          type: 2,
        },
      })
      .then((data) => {
        console.log(data.data);
        setBannerCard(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then(async (data) => {
        console.log(data.data);
        setCategories(data.data);
        let newArray = data.data.filter((item) => {
          return item.is_favorite == true;
        });
        console.log("newArray favv:::", newArray);
        setFavCategories([...newArray]);
        console.log("newArray22", favCategories);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getNewProducts = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          is_new: true,
        },
      })
      .then((data) => {
        console.log(data.data);
        setNewProducts(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getDiscountProducts = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          is_discount: true,
        },
      })
      .then((data) => {
        console.log(data.data);
        setDiscountProducts(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const ScroollRight = (refNewProduct) => {
    refNewProduct.current.scrollBy(200, 0);
  };
  const ScroollLeft = (refNewProduct) => {
    refNewProduct.current.scrollBy(-200, 0);
  };
  return (
    <div className="w-full pb-4">
      {/* <div className="!w-full flex justify-center items-center bg-footerBackground md:h-[630px] h-[230px]"> */}
      <Carousel
        slide="2"
        autoplay
        speed={1000}
        autoplaySpeed={5000}
        dots={false}
        fade={true}
        effect="fade"
        infinite={true}
      >
        {banners.map((item) => {
          console.log("banner", item);
          return (
            <div
              onClick={() => history.push({ pathname: item?.link })}
              className="block w-full px-1 cursor-pointer"
            >
              <div className="!w-full flex justify-center items-center bg-footerBackground md:h-[630px] h-[230px]">
                <img
                  className="md:h-[630px] h-[230px] object-contain"
                  src={BASE_URL + "/" + item?.img}
                  alt=""
                />
              </div>
            </div>
          );
        })}
      </Carousel>
      {/* </div> */}
      <div className="w-full my-8 flex gap-8 justify-start overflow-x-auto scrollbar-hide">
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
        {categories?.map((item) => {
          return <CategoryCard data={item} />;
        })}
      </div>
      <hr className="text-footerBackground bg-footerBackground my-8" />
      <div className="w-full flex my-4 justify-between items-center">
        <h1 className="text-[18px] md:text-[40px] font-[600] text-black">
          Täze gelen harytlar
        </h1>
        <div className="flex gap-4">
          <div
            onClick={() => ScroollLeft(refNewProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[30px] md:w-[44px] h-[30px] md:h-[44px] flex justify-center items-center"
          >
            <LeftOutlined className="text-black  text-[12px] md:text-[16px] font-[900]" />
          </div>
          <div
            onClick={() => ScroollRight(refNewProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[30px] md:w-[44px] h-[30px] md:h-[44px] flex justify-center items-center"
          >
            <RightOutlined className="text-black text-[12px] md:text-[16px] font-[900]" />
          </div>
        </div>
      </div>
      <div
        ref={refNewProduct}
        className="flex gap-4 overflow-x-auto mb-8 md:pb-8 scrollbar-hide"
      >
        {newProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {newProducts?.map((item) => {
          return <Card data={item} />;
        })}
      </div>
      <hr className="text-footerBackground bg-footerBackground my-12" />
      <div className="w-full flex mt-8 my-4 justify-between items-center">
        <h1 className="text-[18px] md:text-[40px] font-[600] text-black">
          Arzanladyşly harytlar
        </h1>
        <div className="flex gap-4">
          <div
            onClick={() => ScroollLeft(refDicountProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[30px] md:w-[44px] h-[30px] md:h-[44px] flex justify-center items-center"
          >
            <LeftOutlined className="text-black text-[12px] md:text-[16px] font-[900]" />
          </div>
          <div
            onClick={() => ScroollRight(refDicountProduct)}
            className="bg-footerBackground cursor-pointer rounded-[10px] w-[30px] md:w-[44px] h-[30px] md:h-[44px] flex justify-center items-center"
          >
            <RightOutlined className="text-black text-[12px] md:text-[16px] font-[900]" />
          </div>
        </div>
      </div>
      <div
        ref={refDicountProduct}
        className="flex gap-4 overflow-x-auto mb-8 md:pb-8 scrollbar-hide"
      >
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
        {discountProducts?.map((item) => {
          return <Card data={item} />;
        })}
      </div>
      <hr className="text-footerBackground bg-footerBackground md:my-12 my-8" />

      <Slider {...settings}>
        {bannerCard.map((item) => {
          return (
            <div className="block">
              {/* <div className="w-full md:my-12 my-0 flex items-center justify-between"> */}
              <img
                className="w-full md:w-[45%] object-contain block rounded-[10px]"
                src={BASE_URL + "/" + item?.img}
                alt="banner"
              />
              {/* </div> */}
            </div>
          );
        })}
      </Slider>
      <hr className="text-footerBackground bg-footerBackground my-12" />
      <div className="w-full flex md:mt-8 mt-2 my-4 justify-between items-center">
        <h1 className="text-[40px] font-[600] text-black">
          Meşhur kategoriýalar
        </h1>
      </div>
      <div className="w-full gap-4 my-8 flex flex-wrap justify-between items-center ">
        {favCategories?.map((item, i) => {
          return (
            <div
              key={"favCat" + i}
              className="w-[49%] cursor-pointer mb-4 h-[150px]  p-3 flex gap-8 items-center bg-footerBackground rounded-[10px]"
            >
              <div className="w-[126px] flex justify-center items-center p-2 h-[126px] bg-white rounded-[7px]">
                <img
                  className="h-[85%] object-contain"
                  src={BASE_URL + "/" + item.img}
                  alt=""
                />
              </div>
              <div>
                <h1 className="text-[24px] font-[600] text-black pb-2">
                  {dil == "tm"
                    ? item.name_tm
                    : dil == "ru"
                    ? item.name_ru
                    : item.name_en}
                </h1>
                <p className="text-[16px] text-black font-[400]">
                  {item?.Products.length} harytlar
                </p>
              </div>
            </div>
          );
        })}
      </div>
      <hr className="text-footerBackground bg-footerBackground my-12" />
      {favCategories?.map((item, i) => {
        return (
          i == 0 && (
            <div>
              <div className="w-full flex mt-8 my-4 justify-between items-center">
                <h1 className="text-[40px] font-[600] text-black">
                  {dil == "tm"
                    ? item?.name_tm
                    : dil == "ru"
                    ? item?.name_ru
                    : item?.name_en}
                </h1>
                <div className="flex gap-4">
                  <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
                    <LeftOutlined
                      onClick={() => ScroollLeft(refCat1)}
                      className="text-black font-[900]"
                    />
                  </div>
                  <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
                    <RightOutlined
                      onClick={() => ScroollRight(refCat1)}
                      className="text-black font-[900]"
                    />
                  </div>
                </div>
              </div>

              <div
                ref={refCat1}
                className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide"
              >
                {item?.Products?.map((itm, j) => {
                  console.log(itm);
                  return <Card data={itm} />;
                })}
              </div>
            </div>
          )
        );
      })}
      <hr className="text-footerBackground bg-footerBackground my-12" />
      <div className="w-full !my-12 flex h-[630px] justify-center items-center bg-footerBackground">
        <img
          className="w-full h-[630px] object-cover"
          src={bannerBig}
          alt="bannerBig"
        />
      </div>
      <hr className="text-footerBackground bg-footerBackground my-12" />

      {favCategories?.map((item, i) => {
        return (
          i != 0 && (
            <div key={"cattall" + i}>
              <div className="w-full flex mt-8 my-4 justify-between items-center">
                <h1 className="text-[40px] font-[600] text-black">
                  {dil == "tm"
                    ? item?.name_tm
                    : dil == "ru"
                    ? item.name_ru
                    : item?.name_en}
                </h1>
                <div className="flex gap-4">
                  <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
                    <LeftOutlined
                      onClick={() => ScroollLeft(refCat2)}
                      className="text-black font-[900]"
                    />
                  </div>
                  <div className="bg-footerBackground cursor-pointer rounded-[10px] w-[44px] h-[44px] flex justify-center items-center">
                    <RightOutlined
                      onClick={() => ScroollRight(refCat2)}
                      className="text-black font-[900]"
                    />
                  </div>
                </div>
              </div>

              <div
                ref={refCat2}
                className="flex gap-4 overflow-x-auto mb-8 pb-8 scrollbar-hide"
              >
                {item?.Products?.map((item) => {
                  return <Card data={item} />;
                })}
              </div>
            </div>
          )
        );
      })}
      <hr className="text-footerBackground bg-footerBackground my-12" />
    </div>
  );
};

export default Home;
