import React, { useContext, useEffect, useState } from "react";
import { axiosInstance, BASE_URL } from "../utils/axiosIntance";
import { Context } from "../context/context";

const Compare = (props) => {
  const { dil } = useContext(Context);
  const [data, setData] = useState({});
  const market = JSON.parse(localStorage.getItem("market"));
  useEffect(() => {
    getCompare();
  }, [props]);
  const getCompare = () => {
    axiosInstance
      .get("/api/compare/all", {
        params: {
          ProductId: props?.ProductId,
        },
      })
      .then((data) => {
        setData(data.data.length > 0 && data.data[0]);
        console.log("compare", data.data.length > 0 && data.data[0]);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="w-full">
      <h1 className="w-full my-[70px] text-[40px] font-[700] text-black text-center">
        {dil == "TM"
          ? data?.name_tm
          : dil == "RU"
          ? data?.name_ru
          : data?.name_en}
      </h1>
      <div className="w-full flex justify-center gap-5">
        {data?.ComparePros?.map((item, i) => {
          return (
            <div key={"comproid" + i} className="w-[300px]">
              <div className="w-[300px] rounded-[6px] h-[300px] bg-[#F9F9F9]">
                <img
                  className="h-[80%] object-contain"
                  src={
                    BASE_URL +
                    "/" +
                    (item?.Product?.ProductImgs.length > 0 &&
                      item?.Product?.ProductImgs[0].src)
                  }
                  alt=""
                />
              </div>
              <h1 className="w-full mt-4 line-clamp-2 text-left text-[18px] font-[600] text-black">
                {dil == "TM"
                  ? item?.Product?.name_tm
                  : dil == "RU"
                  ? item?.Product?.name_ru
                  : item?.Product?.name_en}
              </h1>
              <h1 className="w-full my-6 text-center text-[16px] font-[700] text-black">
                {item?.Product?.is_discount
                  ? item?.Product?.is_valyuta
                    ? (
                        market?.valyuta * item?.Product?.usd_price_discount
                      ).toFixed(0)
                    : item?.Product?.discount_price
                  : item?.Product?.is_valyuta
                  ? (market?.valyuta * item?.Product?.usd_price).toFixed(0)
                  : item?.Product?.price}{" "}
                TMT
              </h1>
              <button className="flex mx-auto px-6 h-[50px] bg-black text-white  text-[16px] font-[500] gap-3 justify-center items-center rounded-[45px]">
                <svg
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    fill-rule="evenodd"
                    clip-rule="evenodd"
                    d="M10.5 17.9927C9.672 17.9927 9 18.6647 9 19.4927C9 20.3207 9.672 20.9927 10.5 20.9927C11.328 20.9927 12 20.3207 12 19.4927C12 18.6647 11.328 17.9927 10.5 17.9927Z"
                    fill="white"
                  />
                  <path
                    fill-rule="evenodd"
                    clip-rule="evenodd"
                    d="M17.5 17.9927C16.672 17.9927 16 18.6647 16 19.4927C16 20.3207 16.672 20.9927 17.5 20.9927C18.328 20.9927 19 20.3207 19 19.4927C19 18.6647 18.328 17.9927 17.5 17.9927Z"
                    fill="white"
                  />
                  <path
                    d="M3 5H5.331C6.297 5 7.126 5.691 7.299 6.642L8.701 14.358C8.874 15.309 9.703 16 10.669 16H17.438C18.356 16 19.156 15.375 19.379 14.485L21 8"
                    stroke="white"
                    stroke-width="2"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  />
                  <path
                    d="M17 7.99268L14 10.9927L11 7.99268"
                    stroke="white"
                    stroke-width="2"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  />
                  <path
                    d="M14 10.9927V4.99268"
                    stroke="white"
                    stroke-width="2"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  />
                </svg>
                Sebede goş
              </button>

              <div className='w-full"'>
                {element?.Product?.ProductParametrs?.map((item, i) => {
                  return (
                    <div key={"parametrs" + i}>
                      <div className="w-full flex justify-between py-8">
                        <div className="w-full flex flex-wrap justify-between">
                          {item?.ProductParametrItems?.map((itm, j) => {
                            return (
                              <div key={"paramItem" + j} className="full my-3">
                                <h1 className="text-[12px] md:text-[18px] font-[600] text-black">
                                  {dil == "TM"
                                    ? itm?.name_tm
                                    : dil == "RU"
                                    ? itm?.name_ru
                                    : itm?.name_en}
                                </h1>
                                <p className="text-[12px] md:text-[18px] font-[500] text-[#868686]">
                                  {dil == "TM"
                                    ? itm?.value_tm
                                    : dil == "RU"
                                    ? itm?.value_ru
                                    : itm?.value_en}
                                </p>
                              </div>
                            );
                          })}
                        </div>
                      </div>
                      <hr className="bg-footerBackground text-footerBackground text-[1px]" />
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>

      {/* <div className="w-full flex justify-center gap-5">
        {data?.ComparePros?.map((element, k) => {
          return <div key={"comproParametr" + k} className="w-[300px]"></div>;
        })}
      </div> */}
    </div>
  );
};

export default Compare;
